function before_action() {
    var auth = (atob(method_auth_value)).split(':');
    var username = auth[0];
    var password = auth[1];
    var domain = method_request_mergefields.ActDomain;
    var databaseName = method_request_headers['Act-Database-Name'];

    method_request_headers.Authorization = 'Bearer ' + generateAuthToken(username, password, domain, databaseName, method_auth_value);

    return true;
}

function generateAuthToken(username, password, domain, databaseName, basic) {
    var request = {
        'method': 'GET',
        'url': 'https://' + domain + '/act.web.api/authorize',
        'headers': {
            'Authorization': 'Basic ' + basic,
            'Act-Database-Name': databaseName,
            'Accept': 'application/json'
        }
    };

    var content = http_request(request).content;
    return content;
}
